class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            'authors': [],
            'users': []
        }
    }

    componentDidMount() {
        axios.get('http://127.0.0.1:8000/api/authors/')
            .then(response => {
                const authors = response.data
                    this.setState(
                    {
                        'authors': authors
                    }
                )
        }).catch(error => console.log(error))
        axios.get('http://127.0.0.1:8000/api/users/')
            .then(response => {
                const users = response.data
                    this.setState(
                    {
                        'users': users
                    }
                )
        }).catch(error => console.log(error))
        const menus = [
            {
                'first': 'Пункт 1',
                'second': 'Пункт 2',
                'third': 'Пункт 3'
            }
        ]
        this.setState(
            {
                'menus': menus
            }
        )
    }

    render () {
        return (
//            <div class='App-header App'>
            <div class='App-header App'>
                <div>
                    <MenuList />
                    Сюда зафигачим меню
                    <hr/>
                </div>
                <div>
                    <br/>
                    Это код с урока 2:
                    <AuthorList authors={this.state.authors} />
                </div>
                <div>
                    <br/>
                    Это код из ДЗ 2:
                    <UserList users={this.state.users} />
                </div>
                <div>
                    <hr/>
                    Тут будет футерок
                </div>
            </div>
        )
    }
}


export default App;


