// ------------------------------------------------------------------

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            'authors': [],
            'users': []
        }
    }

    componentDidMount() {
        axios.get('http://127.0.0.1:8000/api/authors/', 'http://127.0.0.1:8000/api/users/')
            .then(response => {
                const authors = response.data
                    this.setState(
                    {
                        'authors': authors,
                        'users': users
                    }
                )
        }).catch(error => console.log(error))
    }

    render () {
        return (
            <div class='App-header App'>
                <AuthorList authors={this.state.authors} />
                <UserList users={this.state.users} />
            </div>
        )
    }
}


export default App;


